function giveGo(){$(document).scroll(function(){if($(document).scrollTop()>$("#intro").height()){var i=$(this).scrollTop();$("footer").each(function(){var o=$(".media").height()-$("footer").height()-$("nav").height();if(i>o){var t=$(".media").height()-$("footer").height();console.log(t),$(".media aside .aside-inner").css({position:"absolute",top:t})}else $(".media aside").css({position:"fixed",top:"45px",width:"213px"})})}else{var o=$("#options").height(),t=$("#options").width();$(".media aside").css({position:"absolute",width:t,top:$("#intro").height()+$("header").height()})}})}function initGiveGo(){$(window).width()>800&&giveGo()}function initFn(){initGiveGo(),giveGo()}$(function(){initFn()});